import React, { Component } from 'react';
import axios from 'axios';
import './App.css';



class Form extends Component{
  userName = React.createRef();
  state={userName:''};
  handleSubmit= async (event)=>{
    event.preventDefault();
    const resp = await axios.get(`https://api.github.com/users/${this.state.userName.current.value}`)
    this.props.onSubmit(resp.data)
    this.setState({userName:''});

  };  

  render() {
  	return (
      <form onSubmit={this.handleSubmit}>
    	  <input type="text" placeholder="GitHub username" ref={this.userName}/>
        <button>Add card</button>
    	</form>

    );
  }
}

const CardList=(props)=>(
   <div> {props.profiles.map(profile => <Card {...profile}/>)}</div>);

class  Card extends Component{
  render(){
    const profile = this.props;

    return (
    <div>
    <div className="info"/>
    <img  className="img9" src={profile.avatar_url}/> 
    <div className="name">{profile.name}</div>
    <div className="company">{profile.company}</div>  
    </div> 
    );
   }
}
class App extends Component{
  state ={
    profiles:[],
  };

addnewprofile=(profdata)=>{
    this.setState(prevstate=>({
      profiles:[...prevstate.profiles,profdata],
    }));
  };
  render(){
    return (<div>

      <Form onSubmit={this.addnewprofile}/>
      <CardList profiles={this.state.profiles}/>
      </div>
      );
  }
} 

export default App;
